mapper.insert:

insert into product (fspid, name, note, 
  start_date, end_date, price, 
  product_unit, image, gcpid
  )
values (#{fspid,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{note,jdbcType=VARCHAR}, 
  #{startDate,jdbcType=INTEGER}, #{endDate,jdbcType=INTEGER}, #{price,jdbcType=DOUBLE}, 
  #{productUnit,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}, #{gcpid,jdbcType=VARCHAR}
  )

mapper.select:

select fspid, name, note, start_date, end_date, price, product_unit, image, gcpid
from product
where fspid = #{fspid,jdbcType=VARCHAR}
